import React, { useEffect } from 'react';
import styles from './detail_section.module.css'
const DetailSection = (props) => {
    const counter = ($counter, max) => {
        let now = max;
        const handle = setInterval(() => {
            $counter.innerHTML = Math.ceil(max - now);

            // 목표수치에 도달하면 정지
            if (now < 0) {
                clearInterval(handle);
            }

            // 증가되는 값이 계속하여 작아짐
            const step = now / 10;

            // 값을 적용시키면서 다음 차례에 영향을 끼침
            now -= step;
        }, 50);
    }

    useEffect(() => {
        const $counter = document.querySelector(".first");
        // 목표 수치
        const max = 17249;
        setTimeout(() => counter($counter, max), 2000);
        console.log('마운트가 될 경우')

    }, [])



    return (
        <div className={styles.detail_section}>
            <p className={styles.detail_section_p}>  <strong id="first">700만 명</strong>의 여행자</p>
            <p className={styles.detail_section_p}>  <strong id="second">100만 개</strong>의 여행 리뷰</p>
            <p className={styles.detail_section_p}>  <strong id="third">470만 개</strong>의 여행 일정</p>
        </div>);
}
export default DetailSection;
